    public function Symbols128Editing(AcceptanceTester $I)
    {
        $I->amOnPage('/admin/components/run/shop/callbacks');
        $I->click('.//*[@id="callbacks_all"]/table/tbody/tr[1]/td[3]/a');
        $I->waitForElement('.//*[@id="editCallbackForm"]/div[2]/label');
        $I->fillField(CallbacksPage::$UserNameEdit, '12345678901234567890123456789012345678901234567890123456789012345678901234567890123456789012345678901234567890123456789012345678');
        $I->fillField(CallbacksPage::$TelephoneEdit, '12345678901234567890123456789012345678901234567890123456789012345678901234567890123456789012345678901234567890123456789012345678');
        $I->fillField(CallbacksPage::$CommentEdit, '12345678901234567890123456789012345678901234567890123456789012345678901234567890123456789012345678901234567890123456789012345678');
        $I->click(CallbacksPage::$SaveButton);
        $I->waitForElementVisible('.alert.in.fade.alert-success');
        $I->see('Поле Телефон не может превышать 50 символов в длину.');
        $I->waitForElementNotVisible('.alert.in.fade.alert-success');
        $I->fillField(CallbacksPage::$TelephoneEdit, '123456789012345678901234567890123456789012345678901');
        $I->click(CallbacksPage::$SaveButton);
        $I->waitForElementVisible('.alert.in.fade.alert-success');
        $I->see('Поле Телефон не может превышать 50 символов в длину.');
        $I->waitForElementNotVisible('.alert.in.fade.alert-success');
        $I->fillField(CallbacksPage::$TelephoneEdit, '12345678901234567890123456789012345678901234567890');
        $I->click(CallbacksPage::$SaveButton);
        $I->waitForElementVisible('.alert.in.fade.alert-success');
        $I->see('Изменения сохранены');
        $I->waitForElementNotVisible('.alert.in.fade.alert-success');
        $I->seeInField(CallbacksPage::$UserNameEdit, '12345678901234567890123456789012345678901234567890123456789012345678901234567890123456789012345678901234567890123456789012345678');
        $I->seeInField(CallbacksPage::$TelephoneEdit, '12345678901234567890123456789012345678901234567890');
        $I->seeInField(CallbacksPage::$CommentEdit, '12345678901234567890123456789012345678901234567890123456789012345678901234567890123456789012345678901234567890123456789012345678');
        $status=$I->grabValueFrom(CallbacksPage::$StatusSelEdit);
        $I->comment($status);
        $tema=$I->grabValueFrom(CallbacksPage::$ThemeSelEdit);
        $I->comment($tema);
        $date=$I->grabValueFrom(CallbacksPage::$DateEdit);
        $I->comment($date);
        $I->click(CallbacksPage::$GoBackButton);
        $I->waitForText('Список обратных звонков');
        $I->see('12345678901234567890123456789012345678901234567890123456789012345678901234567890123456789012345678901234567890123456789012345678', './/*[@id="callbacks_all"]/table/tbody/tr[1]/td[3]/a');
        $I->see('12345678901234567890123456789012345678901234567890', './/*[@id="callbacks_all"]/table/tbody/tr[1]/td[4]');
        $stat1=$I->grabValueFrom(CallbacksPage::StatusSelListLandingLine('1'));
        $tema1=$I->grabValueFrom(CallbacksPage::ThemeSelListLandingLine('1'));
        $date1=$I->grabTextFrom('.//*[@id="callbacks_all"]/table/tbody/tr[1]/td[7]');
        $I->comment($date1);
        $I->assertEquals($status, $stat1);
        $I->assertEquals($tema, $tema1);
        $I->assertEquals($date, $date1);
        InitTest::ClearAllCach($I);
    } 
    
    
